local a={}local b=require("/CC/Pages/GeneratorPage/Generator")function handleGeneratorButton()if b.getLock()then if b.getStateGenerator()then b.turnOffGenerator()else b.turnOnGenerator()end;b.releaseLock()end end;function a.createFrame(c)local d,e=c:getSize()local f="Generator Manager"local g=math.floor(d/2-#f/2)local h=c:addFrame():setPosition(1,1):setSize("{parent.w}","{parent.h}")local i=h:addLabel():setText(f):setPosition(g,2)local j=h:addLabel():setText("Battery Energy: "..b.getBatteryFillLevel()):setPosition(2,5)local k=h:addButton():setText("Generator is"):setPosition(g,8):onClick(handleGeneratorButton):setBackground(colors.red):setBorder(colors.white):setSize(19,3)local l={title=i,batteryLabel=j,generatorButton=k}return c,h,l,"Generator"end;function a.updateFrame(l)l.batteryLabel:setText("Battery Energy: "..b.getBatteryFillLevel())if b.getLock()then if b.getStateGenerator()then l.generatorButton:setText("Generator is ON"):setBackground(colors.green)else l.generatorButton:setText("Generator is OFF"):setBackground(colors.red)end;b.releaseLock()end end;return a